def get_pokemon_by_name(name: str):
    return next((p for p in POKEMON_DB if p['nome'].lower() == name.lower()), None)


def get_pokemon_by_rarity(rarity: str):
    return [p for p in POKEMON_DB if p['raridade'] == rarity]


def get_pokemon_art_url(pokemon_name: str) -> str:
    name = pokemon_name.lower().replace(' ', '-').replace("'", "")
    return f"https://img.pokemondb.net/artwork/large/{name}.jpg"


def get_pokemon_card_url(pokemon_id: int) -> str:
    return f"https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/{pokemon_id}.png"
